l2jz_tpl['drop.itemMain'] = '<table cellpadding=\"3\" cellspacing=\"0\" width=\"600\" class=\"entityTable\">\r\n	<tr>\r\n		<td colspan=\"3\">@:(new l2jzTable(\'items.oneItem\',{div:\'innerHTML\'})).innerHTML:@</td>\r\n	</tr><tr>\r\n		<td>\r\n			<div class=\"sectionsPanel\">\r\n				&nbsp;Sections:&nbsp;\r\n				@:html.a2(\'item.main.\'+a.item_id):@ <font size=\"-1\">-</font>\r\n				@:((a.rcp_flag>0)||(a.type==\'recipe\'))?html.a2(\'item.recipe.\'+a.item_id)+\' <font size=\"-1\">-</font>\':\'\':@\r\n				@:(a.sll_flag>0)?html.a2(\'item.sell.\'+a.item_id)+\' <font size=\"-1\">-</font>\':\'\':@\r\n				@:(a.drp_flag>0)?html.a2(\'item.drop.\'+a.item_id)+\' <font size=\"-1\">-</font>\':\'\':@\r\n				@:html.a2(\'item.owners.\'+a.item_id):@\r\n				&nbsp;<font size=\"-2\">@:html.h(\'table.items.world_count\'):@:&nbsp;</font>@:(a.world_count>0)?a.world_count:\'0\':@&nbsp;<font size=\"-2\">@:html.h(\'table.items.owners\'):@:&nbsp;</font>@:(a.owners)?a.owners:\'-\':@\r\n				@:html.a(\'map.item.\'+a.item_id,html.h(\'etc.mapStatus.loc\')):@\r\n			</div>\r\n		</td>\r\n	</tr>\r\n</table>';
l2jz_tpl['drop.monsterMain'] = '<table border=\"0\" cellpadding=\"3\" cellspacing=\"0\" width=\"600\" class=\"entityTable\">\r\n	<tr class=\"headTr\">\r\n			<td rowspan=\"11\"><img src=\"modules/drop/i/mobs/@:a.icon:@.jpg\"/></td>\r\n			<td colspan=\"3\" rowspan=\"2\" width=\"250\" class=\"bodyTr\">\r\n				@:a.fHTML.map_flag:@@:a.fHTML.mob_id:@<span class=\"entityName\">@:a.name:@</span><br>\r\n				<font size=\"-2\">@:a.type:@</font><br>\r\n				@:a.fHTML.isUndead:@\r\n			</td>\r\n			<td>@:a.fName.level:@</td>\r\n		</tr><tr class=\"bodyTr\">\r\n			<td>@:a.level:@</td>\r\n		</tr><tr class=\"headTr\">\r\n			<td>@:a.fName.sp:@</td>\r\n			<td>@:a.fName.exp:@</td>\r\n			<td>@:a.fName.sex:@</td>\r\n			<td>@:a.fName.aggro:@</td>\r\n		</tr><tr class=\"bodyTr\">\r\n			<td>@:a.fHTML.sp:@</td>\r\n			<td>@:a.fHTML.exp:@</td>\r\n			<td>@:a.fHTML.sex:@</td>\r\n			<td>@:a.fHTML.aggro:@</td>\r\n		</tr><tr class=\"headTr\">\r\n			<td>@:a.fName.hp:@</td>\r\n			<td>@:a.fName.p_m_atk:@</td>\r\n			<td>@:a.fName.p_m_def:@</td>\r\n			<td>@:a.fName.attackrange:@</td>\r\n		</tr><tr class=\"bodyTr\">\r\n			<td>@:a.hp:@/@:a.mp:@</td>\r\n			<td>@:a.patk:@/@:a.matk:@</td>\r\n			<td>@:a.pdef:@/@:a.mdef:@</td>\r\n			<td>@:a.attackrange:@</td>\r\n		</tr><tr class=\"headTr\">\r\n			<td colspan=\"2\">@:a.fName.p_m_spd:@</td>\r\n			<td colspan=\"2\">@:a.fName.p_m_walkrun:@</td>\r\n		</tr><tr class=\"bodyTr\">\r\n			<td colspan=\"2\">@:a.atkspd:@/@:a.matkspd:@</td>\r\n			<td colspan=\"2\">@:a.walkspd:@/@:a.runspd:@</td>\r\n		</tr><tr class=\"headTr\">\r\n			<td colspan=\"4\">@:html.h(\'table.monster.r_l_hand\'):@</td>\r\n		</tr><tr class=\"bodyTr\">\r\n			<td colspan=\"4\">\r\n				@:(a.rhand>0)?html.a(\'item.main.\'+a.rhand,a.ritem_name):\'Empty\':@/\r\n				@:(a.lhand>0)?html.a(\'item.main.\'+a.lhand,a.litem_name):\'Empty\':@\r\n			</td>\r\n		</tr><tr class=\"bodyTr\" valign=\"bottom\">\r\n			<td colspan=\"11\">\r\n				<div class=\"sectionsPanel\">\r\n					&nbsp;Sections:&nbsp;\r\n					@:html.a2(\'monster.main.\'+a.mob_id):@ <font size=\"-1\">-</font> \r\n					@:html.a2(\'monster.skills.\'+a.mob_id):@\r\n					@:(a.boss_id>0)?\' <font size=\"-1\">-</font>\'+html.a2(\'monster.minions.\'+a.mob_id):\'\':@ \r\n				</div>\r\n			</td>\r\n		</tr>\r\n	</table>';
l2jz_tpl['drop.recipe.calculator'] = '<table border=\"0\" cellpadding=\"1\" cellspacing=\"0\" width=\"350\">\r\n	<tr>\r\n		<td>\r\n			<table class=\"block\" border=\"0\" cellpadding=\"5\" cellspacing=\"1\" width=\"100%\">\r\n				<tr>\r\n					<td nowrap=\"nowrap\" align=\"center\"><b>@:html.h(\'table.recipe.calculator_name\'):@</b></td>\r\n				</tr>\r\n			</table>\r\n		</td>\r\n	</tr><tr>\r\n		<td><div id=\"@:a.recipe_id:@_calculator_body\"></div></td>\r\n	</tr><tr>\r\n		<td>\r\n			<table class=\"sectionsPanel\" cellpadding=\"4\" cellspacing=\"1\" width=\"100%\">\r\n				<tr>\r\n					<td><b>@:html.h(\'table.recipe.total\'):@:&nbsp;</b></td>\r\n					<td id=\"@:a.recipe_id:@_calculator_result\">&nbsp;</td>\r\n					<td align=\"right\" width=\"100%\">@:html.pnl(\'recipe.calculate\',a):@</td>\r\n				</tr>\r\n			</table>\r\n		</td>\r\n	</tr>\r\n</table>';
l2jz_tpl['drop.recipe.calculator_element'] = '<tr>\r\n	<td>&nbsp;</td>\r\n	<td align=\"right\"><b>@:a.count:@</b></td>\r\n	<td align=\"left\" width=\"100%\" nowrap=\"nowrap\">@:html.a(\'item.main.\'+a.item_id,a.name):@</td>\r\n	<td>@:html.pnl(\'recipe.field\',a):@</td>\r\n</tr>';
l2jz_tpl['drop.recipe.component'] = '<table border=\"0\" cellpadding=\"1\" cellspacing=\"0\" name=\"recipe_component\" >\r\n	<tr>\r\n		<td colspan=\"2\" align=\"left\" valign=\"bottom\">\r\n			<input type=\"checkbox\" \r\n						 style=\"color:#D49019;border:1px solid #636563\" \r\n						 name=\"recipe_@:a.recipe_id:@\"\r\n						 parent_id=\"@:a.parent_id:@\"\r\n						 id=\"@:a.this_id:@\"\r\n						 item_id=\"@:a.item_id:@\"\r\n						 count=\"@:a.count*a.recipe_count:@\"\r\n						 OnClick=\"recipe.change_components(\'@:a.recipe_id:@\',this)\"\r\n						 @:a.checked:@\r\n			/>\r\n			<b>@:a.count:@</b> @:html.a(\'item.main.\'+a.item_id,a.name):@\r\n			<font style=\"font-size:10px\">@:(a.count!=1)?\'(\'+a.count*a.recipe_count+\' \'+html.h(\'table.recipe.total\')+\')\':\'\':@</font> <img src=\"modules/drop/i/items_small/@:a.icon:@.png\"/>\r\n		</td>\r\n	</tr><tr>\r\n		<td><img src=\"i/tdot.gif\" width=\"20\" height=\"1\"></td>\r\n		<td id=\"@:a.this_id:@_body\">\r\n			@:(a.body)?recipe.make_recipe(a.body,a.count*a.recipe_count/a.r_count,a.this_id,false):\'\':@\r\n		</td>\r\n	</tr>\r\n</table>';
l2jz_tpl['drop.recipe.main'] = '<table border=\"0\" cellpadding=\"3\" cellspacing=\"0\" width=\"600\" class=\"entityTable\">\r\n	<tr class=\"headTr\">\r\n		<td width=\"32\" valign=\"top\"><img width=\"32\" height=\"32\" src=\"i/items/@:a.icon:@.png\"></td>\r\n		<td align=\"left\" width=\"100%\">\r\n			@:html.a(\'item.recipe.\'+a.sid,a.name):@ @:html.h(\'table.recipe.level\'):@ @:a.recipe_level:@<br>\r\n			@:html.h(\'table.recipe.count\'):@ @:a.count:@, @:html.h(\'table.recipe.chance\'):@ @:a.chance:@%, @:html.h(\'table.recipe.mp\'):@ @:a.mp:@\r\n		</td>\r\n	</tr><tr class=\"bodyTr\">\r\n		<td colspan=\"2\">\r\n			<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\">\r\n				<tr>\r\n					<td name=\"recipe_container\">\r\n						@:recipe.make_recipe(a.body,1,\'recipe_\'+a.id,true):@\r\n					</td>\r\n					<td id=\"@:a.id:@_recipe_calculator\" align=\"right\" valign=\"top\"></td>\r\n				</tr>\r\n			</table>\r\n		</td>\r\n	</tr>\r\n</table>';
